import axios from 'axios';

const apiUrl = "https://localhost:7290"

export default {
  getTasks: async () => {
    const result = await axios.get(`${apiUrl}/items`)    
    console.log(result.data)
    return result.data;
  },

  addTask: async(name)=>{
    console.log('addTask', name)
    const result = await axios.post(`${apiUrl}/items`,{Id:0,Name:name,IsComplete:false})    
    return result.data;
  },

  setCompleted: async(id,name, isComplete)=>{
    console.log('setCompleted', {id, isComplete})
    const result = await axios.put(`${apiUrl}/items`,{Id:id,Name:"",IsComplete:isComplete})    
    return result.data;
  },

  deleteTask:async(id)=>{
    console.log('deleteTask')
    const result = await axios.delete(`${apiUrl}/items/${id}`)    
    return result.data;
  }
};
